select s.std_name,c.course_name,r.rcd
from student s,record r,course c
where s.Std_Id = r.Std_Id and r.course_id = c.course_id

ppd 寫 同下  第四張

SELECT s.std_name, c.course_name, r.rcd
from Student AS s,
     record AS r,
     course AS c
    INNER JOIN Record AS r ON s.Std_Id = r.Std_Id
    INNER JOIN course AS c ON r.course_id = c.course_id
